var haversine = require('../haversine')
  , assert = require('assert');

var arr = [];
var i=0;
var latitude=0;
var longitude=0;
var obj={};
var start ={ "latitude": 0, "longitude": 0};
var time;
var count =0;
for(i=0; i<=999999;){
	if (i==0) {
		console.log("start");
		time = Math.floor(Date.now() /1000);
	}

	latitude+=0.01;
	longitude+=0.01;
	obj ={ "latitude": latitude, "longitude": longitude};
	if(haversine(start,obj) <=100){
		count++;
		i++;
	}else{
		i++;
	}
	if(i==999999){
		time -= Math.floor(Date.now() /1000);
		console.log("time:" + time);
		console.log("num/i:" + count + " "+ i);
		console.log("end");
	}
}
